import React from 'react';
import { useState } from 'react';
import { putNewTopic, getAllTopicMessages } from '../utilities/forum-services';

const StartNewTopic = ({logIn}) => {
    const [title, setTile] = useState('')
    const [text, setText] = useState('')
    console.log('nešto')
    let user_id 
    let username
    let topic_id

    if (logIn) {
        user_id = logIn.user_id
    }

    console.log(user_id)
    console.log(title)

    function handleSubmit() {
        console.log('nešto se deševa')
        putNewTopic({user_id, title})
        .then(data => {
            if (data.success) console.log(data.message)
        })
    }

    getAllTopicMessages()
    .then(data => {
        if (data.success) {
            username = data.messages.username
            topic_id = data.messages.topic_id
            return (username, topic_id)
        }
    })


    return (
        <form>
            <input type="text" placeholder="Započni novu temu" title="Molim vas, proverite prvo u listi sa leve strane da li tema koju pokrećete već postoji" required 
            onInput={e => {setTile(e.target.value)}}/> <br/>
            <input type="text" placeholder="Ostavite prvu poruku na vašoj novoj temi" onInput={e => {setText(e.target.value)}}/> <br/>
            <input type="submit" value="Započni novu temu" onClick={(e) => {e.preventDefault(); handleSubmit()}}/>
        </form>
    )
}

export default StartNewTopic;
