import React from 'react';
import { useState } from 'react';
import { putNewTopic } from '../utilities/forum-services';

const StartNewTopic = ({setUser}) => {
    const [title, setTile] = useState('')
    const [text, setText] = useState('')
    console.log('nešto')
    // let userID = setUser.user_id
    let userID = ''
    function handleSubmit() {
        console.log('nešto se deševa')
        putNewTopic({userID, title})
        .then(data => {
            if (data.success) console.log(data.message)
        })
    }

    return (
        <div>
            <input type="text" placeholder="Započni novu temu" title="Molim vas, proverite prvo u listi sa leve strane da li tema koju pokrećete već postoji" required 
            onInput={e => {setTile(e.target.value)}}/> <br/>
            <input type="text" placeholder="Ostavite prvu poruku na vašoj novoj temi" onInput={e => {setText(e.target.value)}}/> <br/>
            <input type="submit" value="Započni novu temu" onClick={() => handleSubmit()}/>
        </div>
    )
}

export default StartNewTopic;
