import React from 'react'
import { getAllTopics } from '../utilities/forum-services';
import { useState } from 'react';
import { useEffect } from 'react';

const Topics = () => {
    let [topic, setTopic] = useState([]);

    useEffect(()=>{
        getAllTopics()
        .then(data => {
            setTopic = data.topics
            // setTopic = [...topic]
            console.log(setTopic);
        })
    }, [])


    return (
            <ul>
                {topic.map((top) => {return <Topics key={top.topic_id}>{top.title}</Topics>})}
            </ul>
    )
} /*doraditi*/

export default Topics;
