import React from 'react';
import Message from './message';
import { getAllTopicMessages } from '../utilities/forum-services';
import { useState } from 'react';
// ruta /topic-list/{topic_id}
const Chat = ({match, history,  logIn, topic_id}) => {
    const [message, setMessage] = useState('')
    const [messages, setMessages] = useState([])
    let topicID = match.params.topic_id
    let user_id; 
    // let username; 
    // let timestamp;
    // let topic_id;

    if (logIn) {
        user_id = logIn.user_id;
    }


    getAllTopicMessages(topicID)
    .then(data => {
        if(data.success) {
            console.log('dohvaćene poruke');

            setMessages(data.messages)
            // username = data.messages.username;
            // timestamp = data.messages.timestamp;
            // topic_id = data.messages.topic_id;


        }
    })

    return (
        <div>
            <div>

                <label>{}</label>
                {messages.map(mes => { return <Message history={history} user_id={user_id} username={mes.username} message={message} timestamp={mes.timestamp}/>})}          
            </div>

                <div>
                    <input type="text" placeholder="Vaša poruka" required onInput={e => {setMessage(e.target.value)}}/>
                    <input type="submit" value="Pošalji"/>
                </div>

        </div>
    )
}

export default Chat;
