    //dodaj novu poruku i stavi je u setNewMsg
}
console.log(messages)

    return (
        <div>
            <div>

                <label>{}</label>
                {messages.map(mes => { return <Message history={history} user_id={user_id} username={mes.username} message={mes.message} timestamp={mes.timestamp}/>})}          
            </div>

                <div>
                    <input type="text" placeholder="Vaša poruka" required onInput={e => {setMessage(e.target.value)}}/>
