import React from 'react';
import Message from './message';
import { getAllTopicMessages } from '../utilities/forum-services';
import { useState } from 'react';
// ruta /topic-list/{topic_id}
const Chat = ({history,  logIn}) => {
    const [message, setMessage] = useState('')
    let user_id; 
    let username; 
    let timestamp;
    let topic_id;

    if (logIn) {
        user_id = logIn.user_id;
    }


    getAllTopicMessages()
    .then(data => {
        if(data.success) {
            console.log('dohvaćene poruke');
            username = data.messages.username;
            timestamp = data.messages.timestamp;
            topic_id = data.messages.topic_id;


        }
    })

    return (
        <div>
            <div>

                <label>{topic_id}</label>
                <Message history={history} user_id={user_id} username={username} message={message} timestamp={timestamp}/>   
            </div>

                <div>
                    <input type="text" placeholder="Vaša poruka" required onInput={e => {setMessage(e.target.value)}}/>
                    <input type="submit" value="Pošalji"/>
                </div>

        </div>
    )
}

export default Chat;
